package introtojava;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int firstNum = 20;
		int secondNum = 10;
		
		int addOp = firstNum + secondNum;
		int subtractOp = firstNum - secondNum;
		int multiplyOp = firstNum * secondNum;
		int divideOp = firstNum / secondNum;
		
		System.out.println("Add operation result: " + addOp);
		System.out.println("Subtract operation result: " + subtractOp);
		System.out.println("Multiply operation result: " + multiplyOp);
		System.out.println("Divide operation result: " + divideOp);
	}

}
